    public static final Pattern HAS_TAG_PATTERN = Pattern.compile("(?:\\s|\\A|^)[##]+([A-Za-z0-9-_]+)");
    public static final int RECENT_MESSAGE_COUNT = 40;
    public static final int MAX_MESSAGE_LENGTH = 140;
    public static final int MIN_USERNAME_LENGTH = 3;

    public static Boolean isValidEmail(String email){
        return EMAIL_PATTERN.matcher(email).matches();
    }

    public static List<String> extractHashTags(String text){
        List<String> allTags = new ArrayList<String>();
        Matcher m = HAS_TAG_PATTERN.matcher(text);
        while (m.find()) {
            allTags.add(m.group().replaceAll("\\s+",""));
        }
        return allTags;
    }

    /**
     *
     * @return the user for the session on the machine
     */
    public static User sessionUser(){
        if(session("sltSession") == null){
            return null;
        }else{
            return User.getUserFromSession(session("sltSession"));
        }
    }

    /**
     * Generic function to login a user after being authorised
     * @param userID id of user being lo
     */
    public static void loginUser(int userID){
        //check if this user already has an active session somewhere
        String activeSession = User.getUserFromId(userID).getActiveSession();
        //create a new session if none exists
        if(activeSession.equals("")){
            session("sltSession", User.setActiveSession(userID));
        } else{
            //use the existing session, so they aren't kicked off other devices
            session("sltSession", activeSession);
        }
    }

    public static void logoutUser(int userID,Boolean killAll){
        //kill the session on this machine
        session().clear();
        //kills the session for all machines
        if(killAll) {
            User.killActiveSession(userID);
        }
    }


